CREATE TYPE registration_category AS ENUM ('RESIDENT', 'TENANT', 'GUEST'); CREATE TYPE skill_level AS ENUM ('BEGINNER', 'INTERMEDIATE', 'ADVANCED'); CREATE TYPE tshirt_size AS ENUM ('XS', 'S', 'M', 'L', 'XL', 'XXL', 'XXXL'); CREATE TYPE last_played_status AS ENUM ('PLAYING_ACTIVELY', 'NOT_PLAYED_SINCE_LAST_YEAR', 'NOT_PLAYED_IN_FEW_YEARS'); CREATE TABLE IF NOT EXISTS tournament_registrations (id UUID PRIMARY KEY DEFAULT uuid_generate_v4(), first_name TEXT NOT NULL, last_name TEXT NOT NULL, phone_number TEXT NOT NULL, flat_number TEXT NOT NULL, height NUMERIC, registration_category registration_category NOT NULL, registration_type TEXT, playing_positions TEXT[], skill_level skill_level, tshirt_number TEXT, tshirt_name TEXT, tshirt_size tshirt_size, payment_upi_id TEXT, payment_transaction_id TEXT, paid_to TEXT, created_at TIMESTAMPTZ DEFAULT NOW(), updated_at TIMESTAMPTZ DEFAULT NOW(), is_verified BOOLEAN DEFAULT FALSE, last_played_date last_played_status);
